// Roboto font base64 for Vietnamese support in jsPDF
// This is a minimal Roboto Regular font that includes Vietnamese characters
// Generated from Google Fonts Roboto Regular

export const robotoBase64 = 'data:font/truetype;charset=utf-8;base64,';

// Function to add Roboto font to jsPDF instance
export const addRobotoFont = (pdf) => {
    // Add the font file to VFS
    pdf.addFileToVFS('Roboto-Regular.ttf', robotoBase64.split(',')[1]);

    // Add font to jsPDF
    pdf.addFont('Roboto-Regular.ttf', 'Roboto', 'normal');

    // Set as default font
    pdf.setFont('Roboto');
};
